# ================================seriesID = 'GDPC1'file_name = seriesID + '.txt'des_file = seriesID + '_des.txt'dat_file = seriesID + '_dat.txt'# ================================with open(file_name, 'r') as fr:    with open(des_file, 'w') as fw_des:        with open(dat_file, 'w') as fw_dat:            des_flag = 1            for line in fr:                if line[:4] == 'DATE':                    des_flag = 0                if des_flag:                    fw_des.write(line)                else:                    fw_dat.write(line)                    fr.closedfw_des.closedfw_dat.closed